INSERT INTO ricette(title, ingredients, photo, prep_time, number_of_portions,`text`) VALUES ('Cotoletta alla milanese', '4 cotolette di lombata di vitello, 450 g di burro chiarificato, 2 uova, 200 g di pane grattugiato, Sale di Maldon', 'https://i.pinimg.com/564x/06/1e/32/061e323baee2eaa349ef72f130eb01ff.jpg', 90, 6, 'La cotoletta alla milanese è uno dei piatti regionali più amati e diffusi, ben oltre i confini lombardi.');
INSERT INTO ricette(title, ingredients, photo, prep_time, number_of_portions,`text`) VALUES ('Spaghetti allo scoglio', 'Spaghetti 320 g Cozze 1 kg Vongole 1 kg Calamari 300 g Scampi 8 Pomodorini ciliegino 300 g Olio extravergine 4 cucchiai Aglio 1 spicchio Prezzemolo 1 ciuffo Vino bianco 40 g Sale fino q.b. Pepe nero q.b.', 'https://i.pinimg.com/236x/03/c6/bc/03c6bc50a6779573a06f348888e91eb8.jpg', 35, 4, 'Gli spaghetti allo scoglio sono un primo piatto italiano, a base di spaghetti e di frutti di mare come molluschi e crostacei.');
